import{_ as i,I as d,c,o as a,j as l,J as t,an as r,a as n}from"./chunks/framework.Br8X5gKT.js";const b=JSON.parse('{"title":"常见的 Linux 命令","description":"","frontmatter":{},"headers":[],"relativePath":"其他/Linux基本命令.md","filePath":"其他/Linux基本命令.md","lastUpdated":1766135514000}'),u={name:"其他/Linux基本命令.md"};function s(h,e,g,m,p,x){const o=d("ArticleMetadata");return a(),c("div",null,[e[0]||(e[0]=l("h1",{id:"常见的-linux-命令",tabindex:"-1"},[n("常见的 Linux 命令 "),l("a",{class:"header-anchor",href:"#常见的-linux-命令","aria-label":"Permalink to “常见的 Linux 命令”"},"​")],-1)),t(o),e[1]||(e[1]=r('<h3 id="文件与目录操作" tabindex="-1"><strong>文件与目录操作</strong> <a class="header-anchor" href="#文件与目录操作" aria-label="Permalink to “文件与目录操作”">​</a></h3><ul><li><code>ls</code>：列出当前目录下的文件和目录。 <ul><li><code>ls -l</code>：显示详细信息（如权限、大小、修改时间）。</li><li><code>ls -a</code>：显示所有文件，包括隐藏文件。</li></ul></li><li><code>cd</code>：切换当前目录。 <ul><li><code>cd /path/to/directory</code>：跳转到指定目录。</li><li><code>cd ..</code>：跳转到上级目录。</li><li><code>cd ~</code>：跳转到用户的主目录。</li></ul></li><li><code>pwd</code>：显示当前工作目录的完整路径。</li><li><code>mkdir</code>：创建目录。 <ul><li><code>mkdir new-directory</code>：创建名为<code>new-directory</code>的目录。</li></ul></li><li><code>rm</code>：删除文件或目录。 <ul><li><code>rm file.txt</code>：删除文件。</li><li><code>rm -rf directory/</code>：递归删除目录及其内容。</li></ul></li><li><code>cp</code>：复制文件或目录。 <ul><li><code>cp source.txt destination.txt</code>：复制文件。</li><li><code>cp -r source-directory/ destination/</code>：递归复制目录。</li></ul></li><li><code>mv</code>：移动文件或目录，或重命名。 <ul><li><code>mv oldname.txt newname.txt</code>：重命名文件。</li><li><code>mv file.txt /path/to/destination/</code>：移动文件。</li></ul></li></ul><h3 id="文件内容查看与编辑" tabindex="-1"><strong>文件内容查看与编辑</strong> <a class="header-anchor" href="#文件内容查看与编辑" aria-label="Permalink to “文件内容查看与编辑”">​</a></h3><ul><li><code>cat</code>：查看文件内容。 <ul><li><code>cat file.txt</code>：查看<code>file.txt</code>文件的内容。</li></ul></li><li><code>less</code>：分页查看文件内容，支持上下翻页。 <ul><li><code>less file.txt</code>：分页查看文件内容。</li></ul></li><li><code>head</code>：查看文件的前几行。 <ul><li><code>head -n 10 file.txt</code>：查看文件前 10 行。</li></ul></li><li><code>tail</code>：查看文件的后几行。 <ul><li><code>tail -n 10 file.txt</code>：查看文件后 10 行。</li><li><code>tail -f file.txt</code>：实时查看文件新增的内容（常用于查看日志文件）。</li></ul></li><li><code>nano</code>或<code>vim</code>：命令行文本编辑器。 <ul><li><code>nano file.txt</code>：用<code>nano</code>编辑文件（易用）。</li><li><code>vim file.txt</code>：用<code>vim</code>编辑文件（功能强大，学习曲线较陡）。</li></ul></li></ul><h3 id="权限管理" tabindex="-1"><strong>权限管理</strong> <a class="header-anchor" href="#权限管理" aria-label="Permalink to “权限管理”">​</a></h3><ul><li><code>chmod</code>：更改文件或目录的权限。 <ul><li><code>chmod 755 file.txt</code>：给文件设置读、写、执行权限。</li><li><code>chmod +x script.sh</code>：给脚本文件增加执行权限。</li></ul></li><li><code>chown</code>：更改文件或目录的所有者。 <ul><li><code>chown user:group file.txt</code>：将文件的所有者改为<code>user</code>，所属组改为<code>group</code>。</li></ul></li></ul><h3 id="git-与版本控制" tabindex="-1"><strong>Git 与版本控制</strong> <a class="header-anchor" href="#git-与版本控制" aria-label="Permalink to “Git 与版本控制”">​</a></h3><ul><li><code>git clone</code>：克隆远程 Git 仓库到本地。</li><li><code>git pull</code>：从远程仓库拉取最新的更新。</li><li><code>git push</code>：将本地的提交推送到远程仓库。</li><li><code>git commit</code>：提交代码。</li><li><code>git status</code>：查看当前工作区的状态。</li><li><code>git branch</code>：列出所有本地分支。</li><li><code>git checkout</code>：切换到其他分支。</li><li><code>git merge</code>：合并分支。</li><li><code>git log</code>：查看提交历史。</li></ul><h3 id="系统管理与监控" tabindex="-1"><strong>系统管理与监控</strong> <a class="header-anchor" href="#系统管理与监控" aria-label="Permalink to “系统管理与监控”">​</a></h3><ul><li><code>top</code>：查看系统的实时进程和资源使用情况。</li><li><code>htop</code>：<code>top</code>的增强版，图形化界面（需要安装）。</li><li><code>ps</code>：查看当前正在运行的进程。 <ul><li><code>ps aux</code>：查看所有进程。</li></ul></li><li><code>kill</code>：终止进程。 <ul><li><code>kill PID</code>：杀死指定 PID 的进程。</li></ul></li><li><code>df</code>：查看磁盘空间使用情况。 <ul><li><code>df -h</code>：以人类可读的格式显示磁盘空间。</li></ul></li><li><code>free</code>：查看内存使用情况。 <ul><li><code>free -h</code>：以人类可读格式显示内存使用情况。</li></ul></li></ul><h3 id="网络操作" tabindex="-1"><strong>网络操作</strong> <a class="header-anchor" href="#网络操作" aria-label="Permalink to “网络操作”">​</a></h3><ul><li><code>ping</code>：测试网络连接。 <ul><li><code>ping google.com</code>：测试与 Google 的网络连接。</li></ul></li><li><code>curl</code>：获取网页或 API 响应。 <ul><li><code>curl https://api.example.com</code>：获取指定 URL 的内容。</li></ul></li><li><code>wget</code>：从网络下载文件。 <ul><li><code>wget http://example.com/file.zip</code>：下载文件。</li></ul></li><li><code>netstat</code>：查看网络连接。 <ul><li><code>netstat -tuln</code>：查看监听的端口。</li></ul></li><li><code>ssh</code>：远程连接到其他服务器。 <ul><li><code>ssh user@hostname</code>：通过 SSH 连接到远程服务器。</li></ul></li></ul><h3 id="日志查看" tabindex="-1"><strong>日志查看</strong> <a class="header-anchor" href="#日志查看" aria-label="Permalink to “日志查看”">​</a></h3><ul><li><code>tail -f /var/log/nginx/access.log</code>：实时查看 Nginx 访问日志。</li><li><code>journalctl -u service-name</code>：查看特定服务的日志。</li><li><code>grep</code>：在文件中查找特定的文本模式。 <ul><li><code>grep &quot;ERROR&quot; /var/log/nginx/error.log</code>：查找 Nginx 错误日志中的所有<code>ERROR</code>。</li></ul></li></ul><h3 id="包管理" tabindex="-1"><strong>包管理</strong> <a class="header-anchor" href="#包管理" aria-label="Permalink to “包管理”">​</a></h3><ul><li><code>apt</code>（Debian/Ubuntu 系）： <ul><li><code>apt update</code>：更新软件包列表。</li><li><code>apt upgrade</code>：升级已安装的软件包。</li><li><code>apt install package-name</code>：安装指定软件包。</li><li><code>apt remove package-name</code>：卸载指定软件包。</li></ul></li><li><code>yum</code>（CentOS/RHEL 系）： <ul><li><code>yum install package-name</code>：安装指定软件包。</li><li><code>yum update</code>：更新软件包。</li></ul></li></ul><h3 id="前端开发相关" tabindex="-1"><strong>前端开发相关</strong> <a class="header-anchor" href="#前端开发相关" aria-label="Permalink to “前端开发相关”">​</a></h3><ul><li><strong>Node.js 项目管理</strong>： <ul><li><code>npm install</code>：安装项目依赖。</li><li><code>npm run build</code>：执行构建命令（如构建生产环境的代码）。</li><li><code>npm start</code>：启动开发服务器。</li></ul></li><li><strong>查看端口占用情况</strong>： <ul><li><code>lsof -i :3000</code>：查看是否有进程在使用 3000 端口。</li><li><code>kill $(lsof -t -i :3000)</code>：杀掉占用 3000 端口的进程。</li></ul></li><li><strong>使用</strong>**<code>pm2</code>**<strong>管理 Node.js 应用</strong>： <ul><li><code>pm2 start app.js</code>：使用<code>pm2</code>启动 Node.js 应用。</li><li><code>pm2 restart app</code>：重启已启动的应用。</li><li><code>pm2 logs</code>：查看应用日志。</li></ul></li></ul><h3 id="自动化与调度" tabindex="-1"><strong>自动化与调度</strong> <a class="header-anchor" href="#自动化与调度" aria-label="Permalink to “自动化与调度”">​</a></h3><ul><li><code>cron</code>：定时任务调度。 <ul><li>编辑定时任务：<code>crontab -e</code>。</li><li>查看当前定时任务：<code>crontab -l</code>。</li></ul></li><li><code>at</code>：设置一次性任务。 <ul><li><code>at now + 5 minutes</code>：在 5 分钟后执行任务。</li></ul></li></ul>',20))])}const _=i(u,[["render",s]]);export{b as __pageData,_ as default};
